mapper.select:
SELECT CONFIG_NAME, CONFIG_VALUE FROM WM_SYS_CONFIG


		AND CONFIG_NAME = #{CONFIG_NAME}


ORDER BY CONFIG_NAME

SELECT CONFIG_NAME, CONFIG_VALUE, CONFIG_DESCRIPTION FROM WM_SYS_CONFIG A1 LEFT JOIN WM_SYS_CODE A2 ON A1.CONFIG_NAME = A2.CODE_VALUE
AND A1.CONFIG_NAME = #{CONFIG_NAME}
ORDER BY A1.CONFIG_NAME
